import React from 'react';
import { useParams } from 'react-router-dom';
import { Issue } from '../types';
import { addComment, fetchIssueById } from '../services/api';

const IssueDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [issue, setIssue] = React.useState<Issue | null>(null);
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState<string | null>(null);
  const [comment, setComment] = React.useState('');

  React.useEffect(() => {
    const fetchIssue = async () => {
      try {
        if (!id) {
          setError('Invalid issue id.');
          return;
        }
        const fetchedIssue = await fetchIssueById(id);
        setIssue(fetchedIssue);
      } catch (err) {
        setError('Failed to fetch issue details.');
      } finally {
        setLoading(false);
      }
    };

    fetchIssue();
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  if (!issue) {
    return <div>No issue found.</div>;
  }

  return (
    <div>
      <h1>Issue Detail</h1>
      <p>{issue.description}</p>
      {issue.images && issue.images.length > 0 && (
        <img src={issue.images[0]} alt={issue.description} />
      )}
      <div style={{ marginTop: 16 }}>
        <h3>Comments</h3>
        <ul>
          {issue.comments.map((c) => (
            <li key={c.id}>
              <strong>{c.authorName}</strong>: {c.content}
            </li>
          ))}
        </ul>
        <form
          onSubmit={async (e) => {
            e.preventDefault();
            try {
              const updated = await addComment(issue.id, {
                issueId: issue.id,
                authorId: 'me',
                authorName: 'Current User',
                authorRole: 'user',
                content: comment,
                isOfficial: false,
              });
              setIssue(updated);
              setComment('');
            } catch (e) {
              alert('Failed to add comment');
            }
          }}
        >
          <input value={comment} onChange={(e) => setComment(e.target.value)} placeholder="Add a comment" required />
          <button type="submit">Post</button>
        </form>
      </div>
    </div>
  );
};

export default IssueDetail;
