import React from 'react';
import { Link } from 'react-router-dom';
import { Issue } from '../types';
import VoteButtons from './VoteButtons';

interface IssueListProps {
  issues: Issue[];
}

const IssueList: React.FC<IssueListProps> = ({ issues }) => {
  return (
    <div className="container">
      <h2 style={{ textAlign: 'left' }}>Submitted Civic Issues</h2>
      <ul className="list">
        {issues.map((issue) => (
          <li key={issue.id}>
            <h3>
              <Link to={`/issues/${issue.id}`}>{issue.description}</Link>
            </h3>
            <div style={{ display: 'flex', gap: 8, alignItems: 'center', marginBottom: 6 }}>
              <span className="stat">{issue.status.replace('_', ' ')}</span>
              {typeof issue.rewardPoints === 'number' && <span className="stat">Reward: {issue.rewardPoints}</span>}
            </div>
            <VoteButtons issueId={issue.id} upvotes={issue.upvotes} downvotes={issue.downvotes} />
            {issue.images && issue.images.length > 0 && (
              <img
                src={issue.images[0]}
                alt={issue.description}
                style={{ width: '100%', maxHeight: '300px', objectFit: 'cover' }}
              />
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default IssueList;
