import axios from 'axios';
import { Issue } from '../types';

const API_BASE_URL = 'https://your-api-url.com/api'; // Replace with your actual API URL

export const fetchIssues = async (): Promise<Issue[]> => {
  try {
    const response = await axios.get(`${API_BASE_URL}/issues`);
    return response.data;
  } catch (error) {
    const message = error instanceof Error ? error.message : String(error);
    throw new Error('Error fetching issues: ' + message);
  }
};

export const submitIssue = async (issueData: Partial<Issue>): Promise<Issue> => {
  try {
    const response = await axios.post(`${API_BASE_URL}/issues`, issueData);
    return response.data;
  } catch (error) {
    const message = error instanceof Error ? error.message : String(error);
    throw new Error('Error submitting issue: ' + message);
  }
};

export const fetchIssueById = async (id: string): Promise<Issue> => {
  try {
    const response = await axios.get(`${API_BASE_URL}/issues/${id}`);
    return response.data;
  } catch (error) {
    const message = error instanceof Error ? error.message : String(error);
    throw new Error('Error fetching issue: ' + message);
  }
};
