import React from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import L, { DivIcon } from 'leaflet';
import 'leaflet/dist/leaflet.css';
import { Issue, IssueStatus } from '../types';
import { fetchIssues, updateIssueStatus } from '../services/api';

type BackendIssue = {
  id: string | number;
  title: string;
  description: string;
  status: 'Open' | 'In Progress' | 'Resolved' | string;
  type: string;
  location: { lat: number; lng: number };
  address?: string;
  photos?: string[];
  reportedBy?: string;
  date?: string;
};

const statusBackendToFront = (status: string): IssueStatus => {
  switch ((status || '').toLowerCase()) {
    case 'resolved':
      return 'completed';
    case 'in progress':
      return 'in_progress';
    case 'open':
    default:
      return 'not_completed';
  }
};

const statusFrontToBackend = (status: IssueStatus): 'Open' | 'In Progress' | 'Resolved' => {
  switch (status) {
    case 'completed':
      return 'Resolved';
    case 'in_progress':
      return 'In Progress';
    default:
      return 'Open';
  }
};

const getTypeColor = (type: string): string => {
  const key = (type || '').toLowerCase();
  if (key.includes('pothole')) return '#e63946';
  if (key.includes('waste') || key.includes('garbage')) return '#2a9d8f';
  if (key.includes('street') || key.includes('light')) return '#f4a261';
  if (key.includes('water')) return '#457b9d';
  if (key.includes('road')) return '#8d99ae';
  return '#3a86ff';
};

const createTypeIcon = (type: string): DivIcon => {
  const color = getTypeColor(type);
  return L.divIcon({
    className: 'custom-div-icon',
    html: `<div style="width:14px;height:14px;border-radius:50%;background:${color};border:2px solid #fff;box-shadow:0 0 0 2px rgba(0,0,0,.2)"></div>`,
    iconSize: [14, 14],
    iconAnchor: [7, 7]
  });
};

const MapController: React.FC<{ center?: { lat: number; lng: number } }>=({ center }) => {
  const map = useMap();
  React.useEffect(() => {
    if (center) {
      map.setView([center.lat, center.lng], Math.max(map.getZoom(), 14));
    }
  }, [center, map]);
  return null;
};

const OfficialPublicPortal: React.FC = () => {
  const [issues, setIssues] = React.useState<Issue[]>([]);
  const [filtered, setFiltered] = React.useState<Issue[]>([]);
  const [selected, setSelected] = React.useState<Issue | null>(null);
  const [typeFilter, setTypeFilter] = React.useState<string>('all');
  const [statusFilter, setStatusFilter] = React.useState<IssueStatus | 'all'>('all');
  const [loading, setLoading] = React.useState<boolean>(false);
  const [error, setError] = React.useState<string | null>(null);

  const fetchFromBackend = async (): Promise<Issue[]> => {
    try {
      const res = await fetch('/api/issues', { headers: { 'Accept': 'application/json' } });
      if (!res.ok) throw new Error('Backend fetch failed');
      const data: BackendIssue[] = await res.json();
      const mapped: Issue[] = data.map((b) => ({
        id: String(b.id),
        title: b.title,
        description: b.description,
        category: 'other',
        priority: 'medium',
        status: statusBackendToFront(b.status),
        rewardPoints: 0,
        location: {
          address: b.address || 'Unknown',
          coordinates: { lat: b.location?.lat || 0, lng: b.location?.lng || 0 }
        },
        images: b.photos || [],
        reporterId: b.reportedBy || 'unknown',
        reporterName: b.reportedBy || 'Unknown',
        createdAt: b.date ? new Date(b.date) : new Date(),
        updatedAt: b.date ? new Date(b.date) : new Date(),
        comments: [],
        tags: [b.type],
        upvotes: 0,
        downvotes: 0,
        isPublic: true,
      }));
      return mapped;
    } catch (e) {
      throw e;
    }
  };

  const load = React.useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      // Try backend first
      let result: Issue[];
      try {
        result = await fetchFromBackend();
      } catch {
        result = await fetchIssues();
      }
      setIssues(result);
    } catch (e) {
      setError('Failed to load issues');
    } finally {
      setLoading(false);
    }
  }, []);

  React.useEffect(() => {
    load();
  }, [load]);

  React.useEffect(() => {
    let cur = [...issues];
    if (typeFilter !== 'all') {
      cur = cur.filter(i => (i.tags || []).some(t => t.toLowerCase().includes(typeFilter.toLowerCase())));
    }
    if (statusFilter !== 'all') {
      cur = cur.filter(i => i.status === statusFilter);
    }
    setFiltered(cur);
  }, [issues, typeFilter, statusFilter]);

  const center = selected?.location?.coordinates || filtered[0]?.location?.coordinates || { lat: 20.5937, lng: 78.9629 };

  const cycleStatus = async (issue: Issue) => {
    const next: IssueStatus = issue.status === 'not_completed' ? 'in_progress' : issue.status === 'in_progress' ? 'completed' : 'not_completed';
    try {
      const updated = await updateIssueStatus(issue.id, next);
      setIssues(prev => prev.map(i => i.id === issue.id ? updated : i));
      setSelected(s => s && s.id === issue.id ? updated : s);
    } catch {
      // noop
    }
  };

  return (
    <div style={{ display: 'grid', gridTemplateColumns: '380px 1fr', gap: 16, height: '70vh' }}>
      <div style={{ overflow: 'hidden', borderRadius: 8, border: '1px solid var(--border)', display: 'flex', flexDirection: 'column' }}>
        <div style={{ padding: 12, borderBottom: '1px solid var(--border)', display: 'flex', gap: 8 }}>
          <select value={typeFilter} onChange={(e) => setTypeFilter(e.target.value)}>
            <option value="all">All Types</option>
            <option value="pothole">Pothole</option>
            <option value="waste">Waste</option>
            <option value="streetlight">Streetlight</option>
            <option value="water">Water</option>
            <option value="road">Road</option>
          </select>
          <select value={statusFilter} onChange={(e) => setStatusFilter(e.target.value as any)}>
            <option value="all">All Status</option>
            <option value="not_completed">Open</option>
            <option value="in_progress">In Progress</option>
            <option value="completed">Resolved</option>
          </select>
          <button className="btn" onClick={load} disabled={loading}>{loading ? 'Loadingâ€¦' : 'Reload'}</button>
        </div>
        <div style={{ overflowY: 'auto', padding: 12, flex: 1 }}>
          {error && <div className="empty-state"><p>{error}</p></div>}
          {filtered.map((issue) => (
            <div key={issue.id} className="issue-card" style={{ cursor: 'pointer', marginBottom: 12 }} onClick={() => setSelected(issue)}>
              <div className="issue-card-header">
                <div className="issue-title-section">
                  <h3 className="issue-title" style={{ marginBottom: 4 }}>{issue.title}</h3>
                  <div className="issue-meta">
                    <span>{issue.location.address}</span>
                  </div>
                </div>
                <div className="issue-status-badges">
                  <span className={`status-badge ${issue.status === 'completed' ? 'status-resolved' : issue.status === 'in_progress' ? 'status-acknowledged' : 'status-pending'}`}>
                    {issue.status === 'completed' ? 'Resolved' : issue.status === 'in_progress' ? 'In Progress' : 'Open'}
                  </span>
                </div>
              </div>

              {selected?.id === issue.id && (
                <div style={{ marginTop: 8 }}>
                  <div className="issue-description">{issue.description}</div>
                  <div style={{ marginTop: 6, fontSize: 12, color: 'var(--muted)' }}>{issue.location.address}</div>
                  {issue.images && issue.images.length > 0 && (
                    <div style={{ display: 'flex', gap: 8, marginTop: 8, flexWrap: 'wrap' }}>
                      {issue.images.slice(0, 4).map((img, idx) => (
                        <img key={idx} src={img} alt={`photo-${idx}`} style={{ width: 72, height: 72, objectFit: 'cover', borderRadius: 6 }} />
                      ))}
                    </div>
                  )}
                  <div style={{ marginTop: 10, display: 'flex', gap: 8 }}>
                    <button className="btn" onClick={() => cycleStatus(issue)}>Next Status</button>
                    <a className="btn" href={`/issues/${issue.id}`}>View Details</a>
                  </div>
                </div>
              )}
            </div>
          ))}
          {filtered.length === 0 && !loading && (
            <div className="empty-state"><p>No issues found.</p></div>
          )}
        </div>
      </div>

      <div style={{ height: '100%', borderRadius: 8, overflow: 'hidden', border: '1px solid var(--border)' }}>
        <MapContainer center={[center.lat, center.lng]} zoom={13} style={{ height: '100%', width: '100%' }} scrollWheelZoom>
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <MapController center={selected?.location?.coordinates} />
          {filtered.map((issue) => (
            <Marker
              key={issue.id}
              position={[issue.location.coordinates.lat, issue.location.coordinates.lng]}
              icon={createTypeIcon((issue.tags && issue.tags[0]) || '')}
              eventHandlers={{
                click: () => setSelected(issue)
              }}
            >
              <Popup>{issue.title}</Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>
    </div>
  );
};

export default OfficialPublicPortal;
