import React from 'react';
import { useParams } from 'react-router-dom';
import { Issue } from '../types';
import { fetchIssueById } from '../services/api';

const IssueDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [issue, setIssue] = React.useState<Issue | null>(null);
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState<string | null>(null);

  React.useEffect(() => {
    const fetchIssue = async () => {
      try {
        if (!id) {
          setError('Invalid issue id.');
          return;
        }
        const fetchedIssue = await fetchIssueById(id);
        setIssue(fetchedIssue);
      } catch (err) {
        setError('Failed to fetch issue details.');
      } finally {
        setLoading(false);
      }
    };

    fetchIssue();
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  if (!issue) {
    return <div>No issue found.</div>;
  }

  return (
    <div>
      <h1>{issue.title}</h1>
      <p>{issue.description}</p>
      {issue.images && issue.images[0] && <img src={issue.images[0]} alt={issue.description} />}
      <div>
        <p><strong>Status:</strong> {issue.status}</p>
        <p><strong>Category:</strong> {issue.category}</p>
        <p><strong>Priority:</strong> {issue.priority}</p>
        <p><strong>Location:</strong> {issue.location.address}</p>
      </div>
    </div>
  );
};

export default IssueDetail;
