import axios from 'axios';

const API_BASE_URL = 'https://your-api-url.com/api'; // Replace with your actual API URL

export const fetchIssues = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/issues`);
    return response.data;
  } catch (error) {
    throw new Error('Error fetching issues: ' + error.message);
  }
};

export const submitIssue = async (issueData) => {
  try {
    const response = await axios.post(`${API_BASE_URL}/issues`, issueData);
    return response.data;
  } catch (error) {
    throw new Error('Error submitting issue: ' + error.message);
  }
};

export const fetchIssueById = async (id) => {
  try {
    const response = await axios.get(`${API_BASE_URL}/issues/${id}`);
    return response.data;
  } catch (error) {
    throw new Error('Error fetching issue: ' + error.message);
  }
};
