import React, { useState } from 'react';
import UploadImage from './UploadImage';
import { submitIssue } from '../services/api';
import { useNavigate } from 'react-router-dom';

const IssueForm: React.FC = () => {
  const [description, setDescription] = useState('');
  const [image, setImage] = useState<File | null>(null);
  const [submitting, setSubmitting] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    setSubmitting(true);
    try {
      const imageUrl = image ? URL.createObjectURL(image) : undefined;
      const created = await submitIssue({
        id: Math.random().toString(36).slice(2),
        title: description.slice(0, 30) || 'Issue',
        description,
        category: 'other',
        priority: 'low',
        status: 'reported',
        location: {
          address: 'Unknown',
          coordinates: { lat: 0, lng: 0 },
        },
        images: imageUrl ? [imageUrl] : [],
        reporterId: 'me',
        reporterName: 'Current User',
        createdAt: new Date(),
        updatedAt: new Date(),
        comments: [],
        tags: [],
        upvotes: 0,
        downvotes: 0,
        isPublic: true,
      } as any);
      navigate(`/issues/${created.id}`);
    } catch (e) {
      alert('Failed to submit issue');
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="description">Description:</label>
        <textarea
          id="description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
      </div>
      <UploadImage onImageUpload={setImage} />
      <button type="submit" disabled={submitting}>{submitting ? 'Submitting…' : 'Submit Issue'}</button>
    </form>
  );
};

export default IssueForm;
